def create():
    surf=cn.QuadPosSurface()
    grp=cgrp.GroupIng()
    attr=cab.Attribute()
    attr.setAttrType("int")
    attr.setUseMinMax(True)
    attr.setMin(1)
    attr.setMax(32)
    grp.setGrpName("surface_grp")
    for surface in surface_dicts:
        surf.setName(surface["name"])
        surf.setLoctorsFour(surface["locators"])
        grp.addParentObjs(surface["name"])
        surf.create()
        attr.setObj(surface["name"])
        attr.setName("numberV")
        attr.setDefautValue(surface["numberV"])
        attr.addAttr()
        attr.setName("numberU")
        attr.setDefautValue(surface["numberU"])
        attr.addAttr()
    grp.group()

def update():
    surf=cn.QuadPosSurface()
    for surface in surface_dicts:
        surf.setName(surface["name"])
        surf.setLoctorsFour(surface["locators"])
        surf.update()

def main():
    create()
    #update()

